mapper.select:


    and punch_type = #{punchType}
    and del_flag = #{delFlag}
    and user_id = #{userId}
    and create_by = #{createBy}
    and create_time between #{beginCreateTime} and
        #{endCreateTime}

    and update_by = #{updateBy}
    and update_time between #{beginUpdateTime} and
        #{endUpdateTime}


order by STR_TO_DATE(update_time, "%Y-%m-%d %H:%i:%s") desc

SELECT fx67ll_punch_log_result.punch_user, fx67ll_punch_log_result.punch_month, SUM(TIME_TO_SEC(TIMEDIFF(fx67ll_punch_log_result.max_punch_time, fx67ll_punch_log_result.min_punch_time))) / 3600 AS total_work_hours, SUM(TIMESTAMPDIFF(MINUTE, fx67ll_punch_log_result.min_punch_time, fx67ll_punch_log_result.max_punch_time)) AS total_work_minutes, SUM(TIME_TO_SEC(TIMEDIFF(fx67ll_punch_log_result.max_punch_time, fx67ll_punch_log_result.min_punch_time))) AS total_work_seconds, COUNT(*) AS total_punch_days, COUNT(max_punch_time) AS total_work_days, SUM(TIME_TO_SEC(TIMEDIFF(fx67ll_punch_log_result.max_punch_time, fx67ll_punch_log_result.min_punch_time))) / 3600 / COUNT(max_punch_time) AS work_hours_per_day FROM ( SELECT update_by AS punch_user, DATE_FORMAT(update_time, '%Y-%m') AS punch_month, DATE (update_time) AS punch_day, IF(MAX(update_time) != MIN(update_time), MAX(update_time), NULL) AS max_punch_time, IF(MAX(update_time) != MIN(update_time), MIN(update_time), NULL) AS min_punch_time FROM fx67ll_punch_log
and update_by = #{updateBy}
GROUP BY punch_user, punch_month, punch_day ) AS fx67ll_punch_log_result GROUP BY punch_user;

SELECT fx67ll_punch_log_result.punch_user AS punch_user, fx67ll_punch_log_result.punch_month AS punch_month, fx67ll_punch_log_result.punch_day AS punch_day, IF(fx67ll_punch_log_result.punch_type = '2', '上班缺卡', '下班缺卡') AS lost_punch_type FROM ( SELECT punch_type AS punch_type, update_by AS punch_user, DATE_FORMAT(update_time, '%Y-%m') AS punch_month, DATE (update_time) AS punch_day, IF(punch_type = '2', MAX(update_time), MIN(update_time)) AS punch_time FROM fx67ll_punch_log
and update_by = #{updateBy} and punch_month = #{punchMonth}
GROUP BY punch_type, punch_user, punch_month, punch_day ) AS fx67ll_punch_log_result GROUP BY punch_user, punch_day HAVING COUNT(CASE WHEN punch_type = '1' THEN 1 END) = 0 OR COUNT(CASE WHEN punch_type = '2' THEN 1 END) = 0

mapper.update:
update fx67ll_punch_log
punch_type = #{punchType}, punch_remark = #{punchRemark}, del_flag = #{delFlag}, user_id = #{userId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where punch_id = #{punchId}
